import React, { createContext, useContext, useState, useEffect } from 'react';

const VoiceContext = createContext();

export const useVoice = () => {
  const context = useContext(VoiceContext);
  if (!context) {
    throw new Error('useVoice must be used within a VoiceProvider');
  }
  return context;
};

export const VoiceProvider = ({ children }) => {
  const [isListening, setIsListening] = useState(false);
  const [recognition, setRecognition] = useState(null);
  const [synthesis, setSynthesis] = useState(null);

  useEffect(() => {
    console.log('Initializing Voice API...');
    
    // Initialize Web Speech API
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognitionInstance = new SpeechRecognition();
      
      recognitionInstance.continuous = false;
      recognitionInstance.interimResults = false;
      recognitionInstance.lang = 'en-US';
      recognitionInstance.maxAlternatives = 1;
      
      console.log('Speech Recognition initialized');
      setRecognition(recognitionInstance);
    } else {
      console.error('Speech Recognition not supported in this browser');
    }

    if ('speechSynthesis' in window) {
      console.log('Speech Synthesis initialized');
      setSynthesis(window.speechSynthesis);
    } else {
      console.error('Speech Synthesis not supported in this browser');
    }
  }, []);

  const startListening = (onResult, onError) => {
    if (!recognition) {
      onError && onError('Speech recognition not supported');
      return;
    }

    setIsListening(true);
    
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      onResult && onResult(transcript);
      setIsListening(false);
    };

    recognition.onerror = (event) => {
      onError && onError(event.error);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.start();
  };

  const stopListening = () => {
    if (recognition) {
      recognition.stop();
    }
    setIsListening(false);
  };

  const speak = (text, options = {}) => {
    if (!synthesis) {
      console.error('Speech synthesis not supported');
      return;
    }

    // Cancel any ongoing speech
    synthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(text);
    utterance.rate = options.rate || 0.8; // Slower for seniors
    utterance.pitch = options.pitch || 1;
    utterance.volume = options.volume || 1;
    
    if (options.onEnd) {
      utterance.onend = options.onEnd;
    }

    synthesis.speak(utterance);
  };

  const stopSpeaking = () => {
    if (synthesis) {
      synthesis.cancel();
    }
  };

  const value = {
    isListening,
    startListening,
    stopListening,
    speak,
    stopSpeaking,
    isSupported: !!recognition && !!synthesis
  };

  return (
    <VoiceContext.Provider value={value}>
      {children}
    </VoiceContext.Provider>
  );
};
