import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useVoice } from '../context/VoiceContext';
import { useVoiceNarration } from '../context/VoiceNarrationContext';
import { useAuth } from '../context/AuthContext';
import { useWishlist } from '../context/WishlistContext';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { speak } = useVoice();
  const { narrateClick, narrateAddToCart, narrateNavigation } = useVoiceNarration();
  const { isAuthenticated } = useAuth();
  const { toggleWishlist, isInWishlist } = useWishlist();

  const handleProductClick = () => {
    narrateClick(`${product.name} product card`);
    narrateNavigation(`${product.name} details page`);
    speak(`Opening ${product.name} details`);
    navigate(`/product/${product.id}`);
  };

  const handleAddToCart = (e) => {
    e.stopPropagation();
    narrateClick('Add to cart button');
    narrateAddToCart(product.name);
    addToCart(product);
    speak(`Added ${product.name} to cart`);
  };

  const handleWishlistToggle = async (e) => {
    e.stopPropagation();
    console.log('‚ù§Ô∏è Wishlist button clicked for product:', product.name, 'ID:', product.id);
    console.log('üîê Is authenticated:', isAuthenticated);
    
    if (!isAuthenticated) {
      console.log('‚ùå User not authenticated, redirecting to login');
      speak('Please login to add items to wishlist');
      navigate('/login');
      return;
    }
    
    console.log('üì§ Calling toggleWishlist...');
    const result = await toggleWishlist(product.id);
    console.log('üì• Toggle result:', result);
    
    if (result.success) {
      const message = result.inWishlist ? 'Added to wishlist' : 'Removed from wishlist';
      console.log('‚úÖ Success:', message);
      speak(message);
    } else {
      console.error('‚ùå Failed to toggle wishlist:', result.error);
      speak('Failed to update wishlist. Please try again.');
    }
  };

  const inWishlist = isInWishlist(product.id);

  return (
    <div className="product-card" onClick={handleProductClick}>
      {isAuthenticated && (
        <button
          className={`wishlist-icon ${inWishlist ? 'active' : ''}`}
          onClick={handleWishlistToggle}
          title={inWishlist ? 'Remove from wishlist' : 'Add to wishlist'}
        >
          {inWishlist ? '‚ù§Ô∏è' : 'ü§ç'}
        </button>
      )}

      <div className="product-image">
        {product.image_emoji || product.image || 'üì¶'}
      </div>
      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>

        {product.rating && (
          <div className="product-rating">
            <span className="stars">{'‚≠ê'.repeat(Math.round(product.rating))}</span>
            <span className="rating-text">{product.rating}</span>
          </div>
        )}

        <p className="product-price">‚Çπ{product.price}</p>

        <button
          className="add-to-cart-btn"
          onClick={handleAddToCart}
        >
          üõí Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
