import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const BasicVoiceButton = () => {
  const [isListening, setIsListening] = useState(false);
  const [recognition, setRecognition] = useState(null);
  const [lastCommand, setLastCommand] = useState('');
  const [status, setStatus] = useState('Ready');
  const navigate = useNavigate();

  // Initialize speech recognition and synthesis
  useEffect(() => {
    console.log('üé§ Initializing Voice System...');
    
    // Check speech recognition
    if ('webkitSpeechRecognition' in window) {
      const recognitionInstance = new window.webkitSpeechRecognition();
      recognitionInstance.continuous = false;
      recognitionInstance.interimResults = false;
      recognitionInstance.lang = 'en-US';
      
      console.log('‚úÖ Speech Recognition initialized');
      setRecognition(recognitionInstance);
    } else {
      console.error('‚ùå Speech Recognition not supported');
      setStatus('Recognition Not Supported');
      return;
    }

    // Check speech synthesis
    if ('speechSynthesis' in window) {
      console.log('‚úÖ Speech Synthesis available');
      
      // Load voices
      const loadVoices = () => {
        const voices = window.speechSynthesis.getVoices();
        console.log('üé§ Loaded voices:', voices.length);
        if (voices.length > 0) {
          setStatus('Voice Ready - Click üîä to test');
          // Don't auto-speak on load - browsers often block this
          // User must interact first
        } else {
          setStatus('Loading voices...');
        }
      };

      // Load voices immediately if available
      loadVoices();
      
      // Also listen for voice loading event
      window.speechSynthesis.onvoiceschanged = loadVoices;
    } else {
      console.error('‚ùå Speech Synthesis not supported');
      setStatus('Speech Not Supported');
    }
  }, []);

  // Enhanced speech synthesis function with better browser support
  const speak = (text) => {
    console.log('üîä ATTEMPTING TO SPEAK:', text);
    
    if (!text || text.trim() === '') {
      console.log('‚ùå Empty text, skipping speech');
      return;
    }

    if (!('speechSynthesis' in window)) {
      console.error('‚ùå Speech synthesis not supported');
      alert('Speech synthesis not supported. Please use Chrome browser.');
      return;
    }

    try {
      // Force stop any ongoing speech
      window.speechSynthesis.cancel();
      
      // Wait a moment for cancellation to complete
      setTimeout(() => {
        console.log('üîä Creating speech utterance...');
        
        const utterance = new SpeechSynthesisUtterance(text);
        
        // Set speech parameters for maximum compatibility
        utterance.rate = 1.0;
        utterance.pitch = 1.0;
        utterance.volume = 1.0;
        utterance.lang = 'en-US';
        
        // Get and set voice
        const voices = window.speechSynthesis.getVoices();
        console.log('üé§ Available voices:', voices.length);
        
        if (voices.length > 0) {
          // Try different voice selection strategies
          let selectedVoice = null;
          
          // Strategy 1: Look for Google voices
          selectedVoice = voices.find(voice => 
            voice.name.toLowerCase().includes('google') && 
            voice.lang.startsWith('en')
          );
          
          // Strategy 2: Look for Microsoft voices
          if (!selectedVoice) {
            selectedVoice = voices.find(voice => 
              voice.name.toLowerCase().includes('microsoft') && 
              voice.lang.startsWith('en')
            );
          }
          
          // Strategy 3: Any English voice
          if (!selectedVoice) {
            selectedVoice = voices.find(voice => voice.lang.startsWith('en'));
          }
          
          // Strategy 4: First available voice
          if (!selectedVoice) {
            selectedVoice = voices[0];
          }
          
          if (selectedVoice) {
            utterance.voice = selectedVoice;
            console.log('üé§ Selected voice:', selectedVoice.name, selectedVoice.lang);
          }
        } else {
          console.log('‚ö†Ô∏è No voices available, using default');
        }

        // Enhanced event handling with detailed logging
        utterance.onstart = (event) => {
          console.log('‚úÖ SPEECH STARTED SUCCESSFULLY');
          setStatus('üîä Speaking...');
        };
        
        utterance.onend = (event) => {
          console.log('‚úÖ SPEECH COMPLETED SUCCESSFULLY');
        };
        
        utterance.onerror = (event) => {
          console.error('‚ùå SPEECH ERROR:', event.error, event);
          alert(`Speech Error: ${event.error}. Please check your speakers and try again.`);
        };

        utterance.onpause = (event) => {
          console.log('‚è∏Ô∏è Speech paused');
        };

        utterance.onresume = (event) => {
          console.log('‚ñ∂Ô∏è Speech resumed');
        };

        // Start speech synthesis
        console.log('üîä STARTING SPEECH SYNTHESIS...');
        console.log('üîä Text to speak:', text);
        console.log('üîä Utterance settings:', {
          rate: utterance.rate,
          pitch: utterance.pitch,
          volume: utterance.volume,
          lang: utterance.lang,
          voice: utterance.voice ? utterance.voice.name : 'default'
        });
        
        window.speechSynthesis.speak(utterance);
        
        // Verify speech is queued
        setTimeout(() => {
          console.log('üîä Speech synthesis status:', {
            speaking: window.speechSynthesis.speaking,
            pending: window.speechSynthesis.pending,
            paused: window.speechSynthesis.paused
          });
        }, 100);
        
      }, 200); // Wait 200ms after cancel
      
    } catch (error) {
      console.error('‚ùå SPEECH SYNTHESIS ERROR:', error);
      alert(`Speech synthesis error: ${error.message}`);
    }
  };

  // Process voice commands with audio feedback
  const processCommand = (transcript) => {
    console.log('üéØ Processing command:', transcript);
    const command = transcript.toLowerCase().trim();
    setLastCommand(transcript);
    setStatus(`Heard: "${transcript}"`);

    // Test commands
    if (command.includes('hello') || command.includes('test')) {
      console.log('‚úÖ Test command recognized');
      setStatus('‚úÖ Voice Working!');
      speak('Hello! Voice recognition is working perfectly!');
      return;
    }

    // Navigation commands
    if (command.includes('home')) {
      console.log('üè† Navigating to home');
      setStatus('Going to home');
      speak('Going to home page');
      setTimeout(() => navigate('/'), 1000);
      return;
    }

    if (command.includes('cart')) {
      console.log('üõí Navigating to cart');
      setStatus('Opening cart');
      speak('Opening your shopping cart');
      setTimeout(() => navigate('/cart'), 1000);
      return;
    }

    // Search commands
    if (command.includes('search') || command.includes('find')) {
      let searchTerm = '';
      
      if (command.includes('search for')) {
        searchTerm = command.replace('search for', '').trim();
      } else if (command.includes('find')) {
        searchTerm = command.replace('find', '').trim();
      } else if (command.includes('search')) {
        searchTerm = command.replace('search', '').trim();
      }

      if (searchTerm) {
        console.log('üîç Searching for:', searchTerm);
        setStatus(`Searching: ${searchTerm}`);
        speak(`Searching for ${searchTerm}`);
        setTimeout(() => navigate(`/?search=${encodeURIComponent(searchTerm)}`), 1000);
      } else {
        setStatus('What to search?');
        speak('What would you like to search for?');
      }
      return;
    }

    // Category navigation
    if (command.includes('health')) {
      console.log('üè• Opening health category');
      setStatus('Health category');
      speak('Opening health products category');
      setTimeout(() => navigate('/category/health'), 1000);
      return;
    }

    if (command.includes('nutrition')) {
      console.log('ü•ó Opening nutrition category');
      setStatus('Nutrition category');
      speak('Opening nutrition products category');
      setTimeout(() => navigate('/category/nutrition'), 1000);
      return;
    }

    // Help command
    if (command.includes('help')) {
      console.log('‚ùì Help requested');
      setStatus('Help available');
      speak('You can say: hello, go home, show cart, search for vitamins, health category, or nutrition category');
      return;
    }

    // Default response
    console.log('‚ùì Unknown command:', command);
    setStatus(`Unknown: "${transcript}"`);
    speak(`I heard ${transcript}, but I don't understand that command. Try saying hello, go home, show cart, or search for something.`);
  };

  // Start voice recognition with full audio feedback
  const startListening = async () => {
    console.log('üé§ Voice button clicked');
    
    if (!recognition) {
      const msg = 'Voice recognition not supported. Please use Chrome browser.';
      speak(msg);
      alert(msg);
      return;
    }

    if (isListening) {
      console.log('Already listening...');
      speak('Already listening. Please speak your command.');
      return;
    }

    try {
      // Request microphone permission
      await navigator.mediaDevices.getUserMedia({ audio: true });
      console.log('‚úÖ Microphone permission granted');
    } catch (error) {
      console.error('‚ùå Microphone permission denied:', error);
      const msg = 'Please allow microphone access to use voice commands.';
      speak(msg);
      alert(msg);
      return;
    }

    setIsListening(true);
    setStatus('Starting...');
    speak('Starting voice recognition...');

    // Set up event handlers
    recognition.onstart = () => {
      console.log('‚úÖ Voice recognition started');
      setIsListening(true);
      setStatus('üé§ Listening...');
      // Speak the listening message with a delay to avoid conflicts
      setTimeout(() => {
        speak('I am listening. Please speak your command now.');
      }, 500);
    };

    recognition.onresult = (event) => {
      console.log('üìù Voice result received:', event);
      
      if (event.results && event.results.length > 0) {
        const transcript = event.results[0][0].transcript;
        const confidence = event.results[0][0].confidence;
        console.log('üìù Transcript:', transcript);
        console.log('üéØ Confidence:', confidence);
        
        setIsListening(false);
        processCommand(transcript);
      }
    };

    recognition.onerror = (event) => {
      console.error('‚ùå Voice recognition error:', event.error);
      setIsListening(false);
      
      let errorMsg = '';
      let spokenMsg = '';
      
      switch (event.error) {
        case 'not-allowed':
          errorMsg = 'Microphone access denied';
          spokenMsg = 'Microphone access denied. Please allow microphone permission.';
          break;
        case 'no-speech':
          errorMsg = 'No speech detected';
          spokenMsg = 'No speech detected. Please try speaking again.';
          break;
        case 'network':
          errorMsg = 'Network error';
          spokenMsg = 'Network error. Please check your internet connection.';
          break;
        default:
          errorMsg = 'Voice error: ' + event.error;
          spokenMsg = 'Voice recognition error. Please try again.';
      }
      
      setStatus(errorMsg);
      speak(spokenMsg);
      console.log('Setting status to:', errorMsg);
    };

    recognition.onend = () => {
      console.log('üîö Voice recognition ended');
      setIsListening(false);
      if (status === 'üé§ Listening...') {
        setStatus('Ready');
        // Only speak if no command was processed
        if (!lastCommand) {
          speak('Voice recognition ended. Click the microphone to try again.');
        }
      }
    };

    // Start recognition
    try {
      console.log('üé¨ Starting recognition...');
      recognition.start();
    } catch (error) {
      console.error('‚ùå Error starting recognition:', error);
      setIsListening(false);
      setStatus('Error starting');
    }
  };

  if (!recognition) {
    return (
      <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1000 }}>
        <div style={{
          background: '#ff6b6b',
          color: 'white',
          padding: '12px',
          borderRadius: '8px',
          fontSize: '14px'
        }}>
          Voice not supported.<br/>Please use Chrome browser.
        </div>
      </div>
    );
  }

  // Enhanced test speech function with diagnostics
  const testSpeech = () => {
    console.log('üîä TESTING SPEECH SYNTHESIS...');
    
    // Check if speech synthesis is available
    if (!('speechSynthesis' in window)) {
      const msg = 'Speech synthesis not supported in this browser. Please use Chrome or Edge.';
      console.error('‚ùå', msg);
      alert(msg);
      return;
    }

    // Check voices
    const voices = window.speechSynthesis.getVoices();
    console.log('üé§ VOICE DIAGNOSTICS:');
    console.log('- Available voices:', voices.length);
    console.log('- Speech synthesis speaking:', window.speechSynthesis.speaking);
    console.log('- Speech synthesis pending:', window.speechSynthesis.pending);
    console.log('- Speech synthesis paused:', window.speechSynthesis.paused);
    
    if (voices.length > 0) {
      console.log('- Voice list:');
      voices.forEach((voice, index) => {
        console.log(`  ${index}: ${voice.name} (${voice.lang}) - ${voice.default ? 'DEFAULT' : ''}`);
      });
    } else {
      console.log('‚ö†Ô∏è No voices loaded yet, will use default');
    }
    
    // Test with a simple, clear message
    console.log('üîä Starting test speech...');
    speak('Testing. Can you hear this message clearly?');
  };

  return (
    <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1000 }}>
      {/* Test speech button */}
      <button
        onClick={testSpeech}
        style={{
          position: 'absolute',
          bottom: '70px',
          right: '0px',
          width: '40px',
          height: '30px',
          borderRadius: '15px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          fontSize: '12px',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)'
        }}
        title="Test speech output"
      >
        üîä
      </button>

      {/* Main voice button */}
      <button
        onClick={startListening}
        disabled={isListening}
        style={{
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          backgroundColor: isListening ? '#e53e3e' : '#008080',
          color: 'white',
          border: 'none',
          fontSize: '24px',
          cursor: isListening ? 'not-allowed' : 'pointer',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
          animation: isListening ? 'pulse 1s infinite' : 'none'
        }}
        title={isListening ? 'Listening...' : 'Click for voice commands'}
      >
        üé§
      </button>

      {/* Status display */}
      <div style={{
        position: 'absolute',
        bottom: '70px',
        right: '0px',
        background: 'white',
        padding: '6px 10px',
        borderRadius: '6px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        fontSize: '11px',
        whiteSpace: 'nowrap',
        maxWidth: '180px',
        textAlign: 'center',
        border: '1px solid #ddd'
      }}>
        {status}
      </div>

      {/* Last command display */}
      {lastCommand && (
        <div style={{
          position: 'absolute',
          bottom: '100px',
          right: '0px',
          background: '#e8f5e8',
          padding: '4px 8px',
          borderRadius: '4px',
          fontSize: '10px',
          maxWidth: '150px',
          wordBreak: 'break-word',
          border: '1px solid #4CAF50'
        }}>
          Last: "{lastCommand}"
        </div>
      )}

      <style jsx>{`
        @keyframes pulse {
          0% { transform: scale(1); }
          50% { transform: scale(1.1); }
          100% { transform: scale(1); }
        }
      `}</style>
    </div>
  );
};

export default BasicVoiceButton;
