import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useVoiceNarration } from '../context/VoiceNarrationContext';
import { useSpeechCoordination } from '../context/SpeechCoordinationContext';

const BasicVoiceButton = () => {
  const [isVoiceActive, setIsVoiceActive] = useState(false);
  const [isCurrentlyListening, setIsCurrentlyListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [recognition, setRecognition] = useState(null);
  const [lastCommand, setLastCommand] = useState('');
  const [status, setStatus] = useState('Ready');
  const [restartTimeout, setRestartTimeout] = useState(null);
  const navigate = useNavigate();
  const { isNarrationEnabled, toggleNarration, narrateAction } = useVoiceNarration();

  // Simple and reliable voice recognition test
  const testVoiceRecognition = () => {
    console.log('üé§ SIMPLE VOICE RECOGNITION TEST...');
    
    // Check basic support
    if (!('webkitSpeechRecognition' in window)) {
      alert('‚ùå Voice recognition not supported. Please use Chrome browser.');
      return;
    }

    console.log('‚úÖ Voice recognition API available');

    // Request microphone permission first
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      navigator.mediaDevices.getUserMedia({ audio: true })
        .then(() => {
          console.log('‚úÖ Microphone permission granted');
          startSimpleVoiceTest();
        })
        .catch((error) => {
          console.error('‚ùå Microphone permission denied:', error);
          alert('‚ùå Please allow microphone access and try again.');
        });
    } else {
      // Try without explicit permission request
      startSimpleVoiceTest();
    }
  };

  // Start a simple voice recognition test
  const startSimpleVoiceTest = () => {
    try {
      console.log('üé§ Creating simple voice recognition test...');
      
      const testRecognition = new window.webkitSpeechRecognition();
      testRecognition.continuous = false;
      testRecognition.interimResults = false;
      testRecognition.lang = 'en-US';
      
      let testStarted = false;
      
      testRecognition.onstart = () => {
        console.log('‚úÖ VOICE TEST STARTED');
        testStarted = true;
        alert('üé§ Voice test started! Say "hello" clearly now.');
      };
      
      testRecognition.onresult = (event) => {
        if (event.results && event.results.length > 0) {
          const transcript = event.results[0][0].transcript.toLowerCase().trim();
          const confidence = event.results[0][0].confidence;
          console.log('‚úÖ VOICE TEST SUCCESS:', transcript, confidence);
          alert(`‚úÖ Voice recognition WORKING! Heard: "${transcript}" (confidence: ${confidence.toFixed(2)})`);
        }
      };
      
      testRecognition.onerror = (event) => {
        console.error('‚ùå VOICE TEST ERROR:', event.error);
        let errorMsg = 'Voice recognition error: ';
        switch (event.error) {
          case 'not-allowed':
            errorMsg += 'Microphone access denied. Please allow microphone access.';
            break;
          case 'no-speech':
            errorMsg += 'No speech detected. Please speak louder and try again.';
            break;
          case 'network':
            errorMsg += 'Network error. Please check your internet connection.';
            break;
          default:
            errorMsg += event.error;
        }
        alert(`‚ùå ${errorMsg}`);
      };
      
      testRecognition.onend = () => {
        console.log('üîö Voice test ended');
        if (!testStarted) {
          alert('‚ùå Voice recognition failed to start. Please check microphone permissions.');
        }
      };
      
      console.log('üé§ Starting voice recognition test...');
      testRecognition.start();
      
    } catch (error) {
      console.error('‚ùå Exception starting voice test:', error);
      alert(`‚ùå Error: ${error.message}. Please use Chrome browser.`);
    }
  };

  // Initialize speech recognition and synthesis
  useEffect(() => {
    console.log('üé§ Initializing Voice System...');
    
    // Check speech recognition
    if ('webkitSpeechRecognition' in window) {
      const recognitionInstance = new window.webkitSpeechRecognition();
      recognitionInstance.continuous = true; // Enable continuous recognition
      recognitionInstance.interimResults = false;
      recognitionInstance.lang = 'en-US';
      
      console.log('‚úÖ Speech Recognition initialized for continuous mode');
      setRecognition(recognitionInstance);
    } else {
      console.error('‚ùå Speech Recognition not supported');
      setStatus('Recognition Not Supported');
      return;
    }

    // Check speech synthesis
    if ('speechSynthesis' in window) {
      console.log('‚úÖ Speech Synthesis available');
      
      // Load voices
      const loadVoices = () => {
        const voices = window.speechSynthesis.getVoices();
        console.log('üé§ Loaded voices:', voices.length);
        if (voices.length > 0) {
          setStatus('Voice Ready - Narration Auto-Enabled');
        } else {
          setStatus('Loading voices...');
        }
      };

      // Load voices immediately if available
      loadVoices();
      
      // Also listen for voice loading event
      window.speechSynthesis.onvoiceschanged = loadVoices;
    } else {
      console.error('‚ùå Speech Synthesis not supported');
      setStatus('Speech Not Supported');
    }
  }, []);

  // Coordinated speech synthesis that pauses voice recognition
  const speak = (text) => {
    console.log('üîä ATTEMPTING TO SPEAK:', text);
    
    if (!text || text.trim() === '') {
      console.log('‚ùå Empty text, skipping speech');
      return;
    }

    if (!('speechSynthesis' in window)) {
      console.error('‚ùå Speech synthesis not supported');
      return;
    }

    // PAUSE voice recognition during speech to prevent interruption
    if (isVoiceActive && recognition) {
      console.log('‚è∏Ô∏è Pausing voice recognition for speech...');
      try {
        recognition.stop();
        setIsCurrentlyListening(false);
      } catch (error) {
        console.log('Note: Could not pause recognition:', error.message);
      }
    }

    setIsSpeaking(true);

    try {
      // Cancel any ongoing speech
      window.speechSynthesis.cancel();
      
      // Create utterance
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.rate = 0.9;
      utterance.pitch = 1.0;
      utterance.volume = 1.0;
      utterance.lang = 'en-US';
      
      // Get best available voice
      const voices = window.speechSynthesis.getVoices();
      if (voices.length > 0) {
        const englishVoice = voices.find(voice => voice.lang.startsWith('en')) || voices[0];
        utterance.voice = englishVoice;
        console.log('üé§ Using voice:', englishVoice.name);
      }

      // Handle speech events
      utterance.onstart = () => {
        console.log('‚úÖ SPEECH STARTED');
        setStatus('üîä Speaking...');
      };
      
      utterance.onend = () => {
        console.log('‚úÖ SPEECH COMPLETED');
        setIsSpeaking(false);
        
        // RESUME voice recognition after speech completes
        if (isVoiceActive) {
          console.log('‚ñ∂Ô∏è Resuming voice recognition after speech...');
          setTimeout(() => {
            if (isVoiceActive && recognition) {
              try {
                recognition.start();
              } catch (error) {
                console.log('Note: Could not resume recognition:', error.message);
              }
            }
          }, 500); // Wait 500ms before resuming
        }
      };
      
      utterance.onerror = (event) => {
        console.error('‚ùå SPEECH ERROR:', event.error);
        setIsSpeaking(false);
        
        // Resume voice recognition even on error
        if (isVoiceActive) {
          setTimeout(() => {
            if (isVoiceActive && recognition) {
              try {
                recognition.start();
              } catch (error) {
                console.log('Note: Could not resume recognition after error:', error.message);
              }
            }
          }, 500);
        }
      };

      console.log('üîä STARTING SPEECH:', text);
      window.speechSynthesis.speak(utterance);
      
    } catch (error) {
      console.error('‚ùå SPEECH SYNTHESIS ERROR:', error);
      setIsSpeaking(false);
      
      // Resume voice recognition on error
      if (isVoiceActive) {
        setTimeout(() => {
          if (isVoiceActive && recognition) {
            try {
              recognition.start();
            } catch (error) {
              console.log('Note: Could not resume recognition after synthesis error:', error.message);
            }
          }
        }, 500);
      }
    }
  };

  // Process voice commands with audio feedback
  const processCommand = (transcript) => {
    console.log('üéØ Processing command:', transcript);
    const command = transcript.toLowerCase().trim();
    setLastCommand(transcript);
    setStatus(`Heard: "${transcript}"`);

    // Test commands
    if (command.includes('hello') || command.includes('test')) {
      console.log('‚úÖ Test command recognized');
      setStatus('‚úÖ Voice Working!');
      speak('Hello! Voice recognition is working perfectly!');
      return;
    }

    // Navigation commands
    if (command.includes('home')) {
      console.log('üè† Navigating to home');
      setStatus('Going to home');
      speak('Going to home page');
      setTimeout(() => navigate('/'), 1000);
      return;
    }

    if (command.includes('cart')) {
      console.log('üõí Navigating to cart');
      setStatus('Opening cart');
      speak('Opening your shopping cart');
      setTimeout(() => navigate('/cart'), 1000);
      return;
    }

    // Search commands
    if (command.includes('search') || command.includes('find')) {
      let searchTerm = '';
      
      if (command.includes('search for')) {
        searchTerm = command.replace('search for', '').trim();
      } else if (command.includes('find')) {
        searchTerm = command.replace('find', '').trim();
      } else if (command.includes('search')) {
        searchTerm = command.replace('search', '').trim();
      }

      if (searchTerm) {
        console.log('üîç Searching for:', searchTerm);
        setStatus(`Searching: ${searchTerm}`);
        speak(`Searching for ${searchTerm}`);
        setTimeout(() => navigate(`/?search=${encodeURIComponent(searchTerm)}`), 1000);
      } else {
        setStatus('What to search?');
        speak('What would you like to search for?');
      }
      return;
    }

    // Category navigation
    if (command.includes('health')) {
      console.log('üè• Opening health category');
      setStatus('Health category');
      speak('Opening health products category');
      setTimeout(() => navigate('/category/health'), 1000);
      return;
    }

    if (command.includes('nutrition')) {
      console.log('ü•ó Opening nutrition category');
      setStatus('Nutrition category');
      speak('Opening nutrition products category');
      setTimeout(() => navigate('/category/nutrition'), 1000);
      return;
    }

    // Help command
    if (command.includes('help')) {
      console.log('‚ùì Help requested');
      setStatus('Help available');
      speak('You can say: hello, go home, show cart, search for vitamins, health category, or nutrition category');
      return;
    }

    // Default response
    console.log('‚ùì Unknown command:', command);
    setStatus(`Unknown: "${transcript}"`);
    speak(`I heard ${transcript}, but I don't understand that command. Try saying hello, go home, show cart, or search for something.`);
  };

  // Removed complex setupContinuousRecognition - using simpler approach

  // Simple voice recognition toggle
  const toggleVoiceRecognition = async () => {
    console.log('üé§ Simple voice toggle clicked');
    
    if (!recognition) {
      alert('‚ùå Voice recognition not supported. Please use Chrome browser.');
      return;
    }

    if (isVoiceActive) {
      // Turn OFF voice recognition
      console.log('üîá Turning OFF voice recognition');
      setIsVoiceActive(false);
      setIsCurrentlyListening(false);
      setStatus('Voice OFF');
      
      try {
        recognition.stop();
        recognition.abort();
      } catch (error) {
        console.log('Note: Error stopping recognition:', error.message);
      }
      
      speak('Voice recognition turned off');
      
    } else {
      // Turn ON voice recognition
      console.log('üîä Turning ON voice recognition');
      
      try {
        // Request microphone permission
        if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
          await navigator.mediaDevices.getUserMedia({ audio: true });
          console.log('‚úÖ Microphone permission granted');
        }
      } catch (error) {
        console.error('‚ùå Microphone permission denied:', error);
        alert('‚ùå Please allow microphone access to use voice commands.');
        return;
      }

      setIsVoiceActive(true);
      setStatus('Voice ON');
      
      // Setup simple recognition
      setupSimpleRecognition();
      
      speak('Voice recognition activated. Say hello to test.');
    }
  };

  // Simple recognition setup without complex coordination
  const setupSimpleRecognition = () => {
    if (!recognition) return;

    recognition.continuous = true;
    recognition.interimResults = false;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      console.log('‚úÖ Simple voice recognition started');
      setIsCurrentlyListening(true);
      setStatus('üé§ Listening...');
    };

    recognition.onresult = (event) => {
      console.log('üìù Voice result received');
      
      if (event.results && event.results.length > 0) {
        const transcript = event.results[0][0].transcript;
        const confidence = event.results[0][0].confidence;
        console.log('üìù Transcript:', transcript, 'Confidence:', confidence);
        
        processCommand(transcript);
      }
    };

    recognition.onerror = (event) => {
      console.error('‚ùå Voice recognition error:', event.error);
      setIsCurrentlyListening(false);
      
      if (event.error === 'not-allowed') {
        alert('‚ùå Microphone access denied. Please allow microphone access.');
        setIsVoiceActive(false);
        setStatus('Voice OFF');
      } else if (event.error === 'no-speech') {
        console.log('‚ö†Ô∏è No speech detected, continuing...');
      } else {
        console.log('‚ö†Ô∏è Voice error:', event.error, 'continuing...');
      }
    };

    recognition.onend = () => {
      console.log('üîö Voice recognition ended');
      setIsCurrentlyListening(false);
      
      // Auto-restart if still active
      if (isVoiceActive) {
        console.log('üîÑ Restarting voice recognition...');
        setTimeout(() => {
          if (isVoiceActive) {
            try {
              recognition.start();
            } catch (error) {
              console.log('Could not restart recognition:', error.message);
            }
          }
        }, 1000);
      } else {
        setStatus('Voice OFF');
      }
    };

    // Start recognition
    try {
      recognition.start();
    } catch (error) {
      console.error('‚ùå Error starting recognition:', error);
      alert(`‚ùå Error starting voice recognition: ${error.message}`);
      setIsVoiceActive(false);
      setStatus('Voice OFF');
    }
  };

  if (!recognition) {
    return (
      <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1000 }}>
        <div style={{
          background: '#ff6b6b',
          color: 'white',
          padding: '12px',
          borderRadius: '8px',
          fontSize: '14px'
        }}>
          Voice not supported.<br/>Please use Chrome browser.
        </div>
      </div>
    );
  }

  // Comprehensive test speech function
  const testSpeech = () => {
    console.log('üîä COMPREHENSIVE SPEECH TEST...');
    
    // Check browser support
    if (!('speechSynthesis' in window)) {
      alert('‚ùå Speech synthesis not supported. Please use Chrome browser.');
      return;
    }

    // Check voices
    const voices = window.speechSynthesis.getVoices();
    console.log('üé§ SPEECH DIAGNOSTICS:');
    console.log('- Available voices:', voices.length);
    console.log('- Narration enabled:', isNarrationEnabled);
    console.log('- Speech synthesis speaking:', window.speechSynthesis.speaking);
    console.log('- Speech synthesis pending:', window.speechSynthesis.pending);
    
    if (voices.length === 0) {
      console.log('‚ö†Ô∏è No voices loaded, trying to load...');
      // Try to trigger voice loading
      window.speechSynthesis.getVoices();
      setTimeout(() => {
        const newVoices = window.speechSynthesis.getVoices();
        console.log('üé§ Voices after reload:', newVoices.length);
        if (newVoices.length > 0) {
          testActualSpeech();
        } else {
          alert('‚ùå No voices available. Please refresh the page and try again.');
        }
      }, 1000);
    } else {
      testActualSpeech();
    }
  };

  // Test actual speech synthesis
  const testActualSpeech = () => {
    console.log('üîä TESTING ACTUAL SPEECH...');
    
    try {
      // Force cancel any ongoing speech
      window.speechSynthesis.cancel();
      
      const testText = 'Testing speech synthesis. Can you hear this message clearly?';
      const utterance = new SpeechSynthesisUtterance(testText);
      utterance.rate = 1.0;
      utterance.pitch = 1.0;
      utterance.volume = 1.0;
      utterance.lang = 'en-US';
      
      const voices = window.speechSynthesis.getVoices();
      if (voices.length > 0) {
        utterance.voice = voices[0];
        console.log('üé§ Using voice:', voices[0].name);
      }
      
      utterance.onstart = () => {
        console.log('‚úÖ SPEECH TEST STARTED SUCCESSFULLY');
      };
      
      utterance.onend = () => {
        console.log('‚úÖ SPEECH TEST COMPLETED SUCCESSFULLY');
      };
      
      utterance.onerror = (event) => {
        console.error('‚ùå SPEECH TEST ERROR:', event.error);
        alert(`Speech test failed: ${event.error}`);
      };
      
      console.log('üîä STARTING SPEECH TEST...');
      window.speechSynthesis.speak(utterance);
      
    } catch (error) {
      console.error('‚ùå SPEECH TEST EXCEPTION:', error);
      alert(`Speech test exception: ${error.message}`);
    }
  };

  return (
    <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1000 }}>
      {/* Test voice recognition button */}
      <button
        onClick={testVoiceRecognition}
        style={{
          position: 'absolute',
          bottom: '130px',
          right: '0px',
          width: '40px',
          height: '30px',
          borderRadius: '15px',
          backgroundColor: '#9C27B0',
          color: 'white',
          border: 'none',
          fontSize: '12px',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)'
        }}
        title="Test voice recognition"
      >
        üéôÔ∏è
      </button>

      {/* Narration toggle button */}
      <button
        onClick={toggleNarration}
        style={{
          position: 'absolute',
          bottom: '100px',
          right: '0px',
          width: '40px',
          height: '30px',
          borderRadius: '15px',
          backgroundColor: isNarrationEnabled ? '#FF9800' : '#666',
          color: 'white',
          border: 'none',
          fontSize: '12px',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)'
        }}
        title={isNarrationEnabled ? 'Turn OFF action narration' : 'Turn ON action narration'}
      >
        {isNarrationEnabled ? 'üì¢' : 'üîá'}
      </button>

      {/* Test speech button */}
      <button
        onClick={testSpeech}
        style={{
          position: 'absolute',
          bottom: '70px',
          right: '0px',
          width: '40px',
          height: '30px',
          borderRadius: '15px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          fontSize: '12px',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)'
        }}
        title="Test speech output"
      >
        üîä
      </button>

      {/* Main voice toggle button */}
      <button
        onClick={toggleVoiceRecognition}
        style={{
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          backgroundColor: isVoiceActive ? '#4CAF50' : '#666',
          color: 'white',
          border: isVoiceActive ? '3px solid #2E7D32' : '3px solid #444',
          fontSize: '24px',
          cursor: 'pointer',
          boxShadow: isVoiceActive ? '0 4px 15px rgba(76, 175, 80, 0.4)' : '0 4px 12px rgba(0, 0, 0, 0.15)',
          animation: isCurrentlyListening ? 'pulse 1s infinite' : 'none',
          transition: 'all 0.3s ease'
        }}
        title={isVoiceActive ? 'Click to turn OFF voice recognition' : 'Click to turn ON continuous voice recognition'}
      >
        {isVoiceActive ? 'üé§' : 'üîá'}
      </button>

      {/* Status display */}
      <div style={{
        position: 'absolute',
        bottom: '70px',
        right: '0px',
        background: isVoiceActive ? '#E8F5E8' : 'white',
        padding: '6px 10px',
        borderRadius: '6px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        fontSize: '11px',
        whiteSpace: 'nowrap',
        maxWidth: '200px',
        textAlign: 'center',
        border: isVoiceActive ? '1px solid #4CAF50' : '1px solid #ddd',
        color: isVoiceActive ? '#2E7D32' : '#333'
      }}>
        {status}
      </div>

      {/* Voice mode indicator */}
      {isVoiceActive && (
        <div style={{
          position: 'absolute',
          bottom: '100px',
          right: '0px',
          background: isCurrentlyListening ? '#4CAF50' : '#FFC107',
          color: 'white',
          padding: '4px 8px',
          borderRadius: '4px',
          fontSize: '10px',
          fontWeight: 'bold',
          textAlign: 'center',
          animation: isCurrentlyListening ? 'pulse 1s infinite' : 'none'
        }}>
          {isCurrentlyListening ? 'üé§ LISTENING' : '‚è∏Ô∏è READY'}
        </div>
      )}

      {/* Last command display */}
      {lastCommand && (
        <div style={{
          position: 'absolute',
          bottom: isVoiceActive ? '130px' : '100px',
          right: '0px',
          background: '#e8f5e8',
          padding: '4px 8px',
          borderRadius: '4px',
          fontSize: '10px',
          maxWidth: '150px',
          wordBreak: 'break-word',
          border: '1px solid #4CAF50'
        }}>
          Last: "{lastCommand}"
        </div>
      )}

      <style jsx>{`
        @keyframes pulse {
          0% { transform: scale(1); }
          50% { transform: scale(1.1); }
          100% { transform: scale(1); }
        }
      `}</style>
    </div>
  );
};

export default BasicVoiceButton;
