import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const UltraSimpleVoice = () => {
  const [isListening, setIsListening] = useState(false);
  const [lastCommand, setLastCommand] = useState('');
  const recognitionRef = useRef(null);
  const navigate = useNavigate();

  const speak = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.rate = 0.9;
    window.speechSynthesis.speak(utterance);
  };

  const processCommand = (text) => {
    const command = text.toLowerCase();
    console.log('===== VOICE COMMAND =====');
    console.log('Raw text:', text);
    console.log('Lowercase:', command);
    setLastCommand(text);

    // Simple command matching with detailed logging
    if (command.includes('hello') || command.includes('hi')) {
      console.log('âœ… MATCHED: hello/hi');
      speak('Hello! Voice is working!');
    } else if (command.includes('cart')) {
      console.log('âœ… MATCHED: cart');
      speak('Opening cart');
      setTimeout(() => navigate('/cart'), 500);
    } else if (command.includes('home')) {
      console.log('âœ… MATCHED: home');
      speak('Going home');
      setTimeout(() => navigate('/'), 500);
    } else if (command.includes('health')) {
      console.log('âœ… MATCHED: health');
      speak('Opening health category');
      setTimeout(() => navigate('/category/health'), 500);
    } else {
      console.log('âŒ NO MATCH - saying what was heard');
      speak('I heard ' + text);
    }
    console.log('========================');
  };

  const startListening = () => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Voice not supported. Use Chrome browser.');
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = false;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      console.log('Started listening');
      setIsListening(true);
    };

    recognition.onresult = (event) => {
      const transcript = event.results[event.results.length - 1][0].transcript;
      console.log('Heard:', transcript);
      processCommand(transcript);
    };

    recognition.onerror = (event) => {
      console.log('Error:', event.error);
      if (event.error !== 'aborted' && event.error !== 'no-speech') {
        setIsListening(false);
      }
    };

    recognition.onend = () => {
      console.log('Ended');
      if (recognitionRef.current) {
        setTimeout(() => {
          try {
            recognition.start();
          } catch (e) {
            console.log('Restart failed:', e.message);
          }
        }, 1000);
      }
    };

    recognitionRef.current = recognition;
    recognition.start();
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      recognitionRef.current = null;
      setIsListening(false);
    }
  };

  const toggleVoice = () => {
    if (isListening) {
      stopListening();
      speak('Voice off');
    } else {
      startListening();
      speak('Voice on');
    }
  };

  return (
    <div style={{
      position: 'fixed',
      bottom: '20px',
      right: '20px',
      zIndex: 1000
    }}>
      <button
        onClick={toggleVoice}
        style={{
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          backgroundColor: isListening ? '#4CAF50' : '#666',
          color: 'white',
          border: 'none',
          fontSize: '24px',
          cursor: 'pointer',
          boxShadow: '0 4px 12px rgba(0,0,0,0.3)'
        }}
      >
        {isListening ? 'ðŸŽ¤' : 'ðŸ”‡'}
      </button>
      
      {lastCommand && (
        <div style={{
          position: 'absolute',
          bottom: '70px',
          right: '0',
          background: 'white',
          padding: '8px',
          borderRadius: '4px',
          fontSize: '12px',
          boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
          maxWidth: '200px'
        }}>
          {lastCommand}
        </div>
      )}
    </div>
  );
};

export default UltraSimpleVoice;
